import { Component } from '@angular/core';

@Component({
  selector: 'app-password-strength',
  templateUrl: './password-strength.component.html',
  styleUrls: ['./password-strength.component.css'],
})
export class PasswordStrengthComponent {
  password: string = '';

  calculateStrength() {
    // No need to perform calculations here since the strength is determined by CSS classes
  }
}
<div>
  <input
    type="password"
    [(ngModel)]="password"
    (input)="calculateStrength()"
    placeholder="Enter Password"
  />
  <div
    class="section"
    [ngClass]="{
      gray: !password,
      red: password && password.length < 8,
      yellow: password && password.length >= 8 && password.length < 12,
      green: password && password.length >= 12
    }"
  >
    <div></div>
    <div></div>
    <div></div>
  </div>
</div>
