import { Component } from '@angular/core';

@Component({
  selector: 'app-password-strength',
  templateUrl: './password-strength.component.html',
  styleUrls: ['./password-strength.component.css'],
})
export class PasswordStrengthComponent {
  password: string = '';
  hidePassword: boolean = true;
  currentColor: string = 'lightgrey';

  calculateStrength() {
    // No need to perform calculations here, the strength is determined by CSS classes
  }

  togglePasswordVisibility() {
    this.hidePassword = !this.hidePassword;
  }

  getPasswordStrengthPercentage() {
    if (!this.password) {
      return 0;
    } else if (this.password.length < 8) {
      return 33;
    } else if (this.password.length >= 8 && this.password.length < 12) {
      return 66;
    } else {
      return 100;
    }
  }

  getProgressBarColor() {
    if (this.getPasswordStrengthPercentage() === 100) {
      return 'green';
    } else if (this.getPasswordStrengthPercentage() >= 66) {
      return 'yellow';
    } else {
      return 'red';
    }
  }

  getProgressBarClass() {
    if (this.getPasswordStrengthPercentage() === 100) {
      return 'green-progress';
    } else if (this.getPasswordStrengthPercentage() >= 66) {
      return 'yellow-progress';
    } else {
      return 'red-progress';
    }
  }
}
<div class="container">
  <div>
    <mat-form-field class="example-full-width">
      <mat-label>Password</mat-label>
      <input
        type="{{ hidePassword ? 'password' : 'text' }}"
        matInput
        [(ngModel)]="password"
        (input)="calculateStrength()"
        placeholder="Enter Password"
      />
      <button mat-icon-button matSuffix (click)="togglePasswordVisibility()">
        <mat-icon>{{
          hidePassword ? 'visibility_off' : 'visibility'
        }}</mat-icon>
      </button>
      <mat-hint>Password strength will be evaluated instantly!</mat-hint>
    </mat-form-field>
  </div>

  <mat-progress-bar
    mode="determinate"
    [value]="getPasswordStrengthPercentage()"
    [class]="getProgressBarClass()"
  ></mat-progress-bar>

  <div class="strength-sections">
    <div
      class="strength-section lightgray"
      [ngClass]="{ red: password && password.length < 8 }"
    >
      <div></div>
      <span>Weak</span>
    </div>
    <div
      class="strength-section lightgray"
      [ngClass]="{
        yellow: password && password.length >= 8 && password.length < 12
      }"
    >
      <div></div>
      <span>Medium</span>
    </div>
    <div
      class="strength-section lightgray"
      [ngClass]="{ green: password && password.length >= 12 }"
    >
      <div></div>
      <span>Strong</span>
    </div>
  </div>
</div>
